import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBoxOpen, faCartArrowDown, faChartPie, faChevronDown, faClipboard, faCommentDots, faFileAlt, faPlus, faRocket, faStore } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Card,Form,Button, Dropdown } from '@themesberg/react-bootstrap';
import { ChoosePhotoWidget, ProfileCardWidget } from "../components/Widgets";
import { GeneralInfoForm } from "../components/Forms";

import Profile3 from "../assets/img/team/profile-picture-3.jpg";


export default () => {


    useEffect(()=>{
        fetch(`FeedUpApp/`,{
         method:"PUT",
         headers:{
             Authorization: `Bearer ${user.access_token}`,
             "content-type":"application/json"}
         }).then((res)=>{
            return res.json();
        }).then((resp)=>{
         labchange(resp.lab);
         standchange(resp.standards);
         recommchange(resp.recomm);
         querychange(resp.query);
         commentschange(resp.comments);
        }).catch((err)=>{
            console.log(err.message);
        })
    },[]);
 function handleSubmit(e){
   e.preventDefault();
   fetch(`FeedUpApp/` +id,{
         method:"PUT",
         headers:{
             Authorization: `Bearer ${user.access_token}`,
             "content-type":"application/json"},
             body:JSON.stringify(data)

     })
     .then(res=>{
        alert('application updated');

        history.push('/industry/evaluator2');

     }).catch((err) =>{
         console.log(err.message)
     })

 }
  return (
    <>


      <Row>
        <Col xs={12} xl={8}>
        <Card border="light" className="bg-white shadow-sm mb-4">
      <Card.Body>
        <h5 className="mb-4">Change Password</h5>
        <Form>
          <Row>
            <Col md={6} className="mb-3">
              <Form.Group id="firstName">
                <Form.Label>Old Password</Form.Label>
                <Form.Control required type="password" placeholder="Enter your old password" />
              </Form.Group>
            </Col>
            <Col md={6} className="mb-3">
              <Form.Group id="lastName">
                <Form.Label>New Password</Form.Label>
                <Form.Control required type="text" placeholder="New Password" />
              </Form.Group>
              <Form.Group id="lastName">
                <Form.Label> Confirm New Password</Form.Label>
                <Form.Control required type="text" placeholder="confirm new password" />
              </Form.Group>
            </Col>
          </Row>



          <div className="mt-3">
            <Button variant="primary" type="submit">Change</Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
        </Col>

        <Col xs={12} xl={4}>
          <Row>
            <Col xs={12}>
              <ProfileCardWidget />
            </Col>
            <Col xs={12}>
              <ChoosePhotoWidget
                title="Select profile photo"
                photo={Profile3}
              />
            </Col>
          </Row>
        </Col>
      </Row>
    </>
  );
};
