import React, { useState ,useEffect} from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleLeft, faEnvelope, faMobile, faUserCircle } from "@fortawesome/free-solid-svg-icons";
import { Col, Row, Form, Card, Button, FormCheck, Container, InputGroup } from '@themesberg/react-bootstrap';
import { Link ,useHistory,useParams} from 'react-router-dom';

export default () => {
    const {id} = useParams();
    const[common_name,commonchange] = useState("");
    const[product_image,productchange] = useState("");
    const[food_ingredient_image,ingrechange] = useState("");
    const[food_category,categorychange] = useState("");
    const history = useHistory();
    const user = JSON.parse(localStorage.getItem("token"));
    const[lab,labchange] = useState("");
    const[standards,standchange] = useState("");
    const[recomm,recommchange] = useState("");
    const[query,querychange] = useState("");
    const[comments,commentschange] = useState("");
    const data ={lab,standards,recomm,query,comments};

        console.log(id);
       useEffect(()=>{
           fetch(`FeedUpApp/` +id).then((res)=>{
               return res.json();
           }).then((resp)=>{
            labchange(resp.lab);
            standchange(resp.standards);
            recommchange(resp.recomm);
            querychange(resp.query);
            commentschange(resp.comments);
           }).catch((err)=>{
               console.log(err.message);
           })
       },[]);
    function handleSubmit(){

      fetch(`FeedUpApp/` +id,{
            method:"PUT",
            headers:{
                Authorization: `Bearer ${user.access_token}`,
                "content-type":"application/json"},
                body:JSON.stringify(data)

        })
        .then(res=>{
           alert('application updated');

           history.push('/industry/evaluator2');

        }).catch((err) =>{
            console.log(err.message)
        })

    }
  return (
    <main>
      <section className="d-flex align-items-center my-5 mt-lg-6 mb-lg-5">
        <Container>

          <Row className="justify-content-center form-bg-image" >
            <Col xs={8} className="d-flex align-items-center justify-content-center">
              <div className="mb-4 mb-lg-0 bg-white shadow-soft border rounded border-light p-4 p-lg-5 w-100 fmxw-500">
                <div className="text-center text-md-center mb-4 mt-md-0">
                  <h3 className="mb-0">Evaluation Form</h3>
                </div>
                <Form className="mt-4" onSubmit={handleSubmit}>

                </Form>


              </div>
            </Col>

          </Row>

        </Container>
      </section>
    </main>
  );
};
