import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBuilding, faCashRegister, faChartLine, faCloudUploadAlt, faPlus, faRocket, faTasks, faUserFriends, faUserShield } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Card,Table,Button, Dropdown, ButtonGroup } from '@themesberg/react-bootstrap';
import {useEffect ,useState} from 'react';
import { CounterWidget, CircleChartWidget, BarChartWidget, TeamMembersWidget, ProgressTrackWidget, RankingWidget, SalesValueWidget, SalesValueWidgetPhone, AcquisitionWidget } from "../../components/Widgets";
import { UsersTable } from "../../components/Users";
import { trafficShares, totalOrders } from "../../data/charts";
import axios from 'axios';
import{useHistory} from 'react-router-dom';

export default () => {
  const [userdata,userdatachange] = useState([]);
  const [inddata,inddatachange] = useState([]);
  const user = JSON.parse(localStorage.getItem("token"));
  var u_count;
  var i_count;
  const fetchData = () => {fetch("http://127.0.0.1:8000/apiaccounts/countusers/",{
    method:"GET",
    headers:{
      Authorization: `Bearer ${user.access_token}`,
      "content-type":"application/json"}
  }).then((res)=>{
    return res.json();
  }).then((resp)=>{
   userdatachange(resp);
  }).catch((err)=>{
    console.log(err.message);
  })

}
const Data = () => {fetch("http://127.0.0.1:8000/apiaccounts/countindustry/",{
    method:"GET",
    headers:{
      Authorization: `Bearer ${user.access_token}`,
      "content-type":"application/json"}
  }).then((res)=>{
    return res.json();
  }).then((resp)=>{
   inddatachange(resp);
  }).catch((err)=>{
    console.log(err.message);
  })

}
  useEffect(()=>{
    Data();
    fetchData();
  },[]);
  u_count = userdata;
  i_count = inddata;

  return (
    <>
      <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center py-4">
        <Dropdown className="btn-toolbar">
          <Dropdown.Toggle as={Button} variant="primary" size="sm" className="me-2">
            <FontAwesomeIcon icon={faPlus} className="me-2" />New Task
          </Dropdown.Toggle>
          <Dropdown.Menu className="dashboard-dropdown dropdown-menu-left mt-2">
            <Dropdown.Item className="fw-bold">
              <FontAwesomeIcon icon={faTasks} className="me-2" /> New Task
            </Dropdown.Item>
            <Dropdown.Item className="fw-bold">
              <FontAwesomeIcon icon={faCloudUploadAlt} className="me-2" /> Upload Files
            </Dropdown.Item>
            <Dropdown.Item className="fw-bold">
              <FontAwesomeIcon icon={faUserShield} className="me-2" /> Preview Security
            </Dropdown.Item>

            <Dropdown.Divider />

            <Dropdown.Item className="fw-bold">
              <FontAwesomeIcon icon={faRocket} className="text-danger me-2" /> Upgrade to Pro
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>

        <ButtonGroup>
          <Button variant="outline-primary" size="sm">Share</Button>
          <Button variant="outline-primary" size="sm">Export</Button>
        </ButtonGroup>
      </div>

      <Row className="justify-content-md-center">


        <Col xs={12} sm={6} xl={4} className="mb-4">

          <CounterWidget

            category="Users"
            title={u_count}
            period="Feb 1 - Jun 1"
            icon={faUserFriends}
            iconColor="shape-secondary"
          />

        </Col>


        <Col xs={12} sm={6} xl={4} className="mb-4">
          <CounterWidget
            category="Industry"
            title={i_count}
            period="Feb 1 - Jun 1"
            icon={faBuilding}
            iconColor="shape-tertiary"
          />
        </Col>

        <Col xs={12} sm={6} xl={4} className="mb-4">
          <CircleChartWidget
            title="Traffic Share"
            data={trafficShares} />
        </Col>
      </Row>

      <Row>
        <Col xs={12} xl={12} className="mb-4">
          <Row>
            <Col xs={12} xl={8} className="mb-4">
              <Row>
                <Col xs={12} className="mb-4">
                  <UsersTable />
                </Col>




              </Row>
            </Col>

            <Col xs={12} xl={12}>
              <Row>

              <Card border="light" className="shadow-sm mb-0 p-4 p-lg-5 w-100 fmxw-300">
        <Card.Body className="pb-1">
        <div className="text-center text-md-center mb-4 mt-md-0">
                  <h5 className="mb-0">List Of Roles</h5>
                </div>
          <Table responsive className="table-centered  rounded mb-4">
            <thead className="thead-light">
            <tr>
                <th className="border-0">#</th>
                <th className="border-0"> Type Name</th>
                <th className="border-0">Action</th>
              </tr>
            </thead>
            <tbody>
              {/* {userdata.map((item)=>( */}
                  <tr >
                  <td></td>
                  <td></td>
                  <td>
                      <Button  size="sm" variant ="success" className="m-1">Details</Button>
                      <Button size="sm" variant="primary"    className="m-1">Delete</Button>
                  </td>

                  </tr>
              {/* ))} */}

            </tbody>
          </Table>
        </Card.Body>
        </Card>



              </Row>
            </Col>
          </Row>
        </Col>
      </Row>
    </>
  );
};
