import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleDown, faAngleUp, faEnvelope, faMobile, faUserCircle,faArrowDown, faArrowUp, faEdit, faEllipsisH, faExternalLinkAlt, faEye, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Nav,Form,Modal ,Card, Image, Button,InputGroup, Table, Dropdown, ProgressBar, Pagination, ButtonGroup } from '@themesberg/react-bootstrap';
import { Link,useHistory, useParams } from 'react-router-dom';

import { Routes } from "../routes";
import { pageVisits, pageTraffic, pageRanking } from "../data/tables";
import transactions from "../data/transactions";
import commands from "../data/commands";
import {useEffect,useState} from 'react';
export const AppTable = () => {

    const history = useHistory();
    const [userdata,userdatachange] = useState([]);
    const [appdata,appdatachange] = useState([]);
    const user = JSON.parse(localStorage.getItem("token"));
    const [showDefault, setShowDefault] = useState(false);
    const handleClose = () => setShowDefault(false);
    const[common_name,commonchange] = useState("");
    const[product_image,productchange] = useState("");
    const[food_ingredient_image,ingrechange] = useState("");
    const[food_category,categorychange] = useState("");
    const data ={common_name,product_image,food_ingredient_image,food_category};
   const fetchData = () => {fetch("http://127.0.0.1:8000/apiaccounts/Listofapp/",{
    method:"GET",
    headers:{
      Authorization: `Bearer ${user.access_token}`,
      "content-type":"application/json"}
  }).then((res)=>{
    return res.json();
  }).then((resp)=>{
    userdatachange(resp);
  }).catch((err)=>{
    console.log(err.message);
  })};
   const loadEdit =(id)=>{

    const resp =    fetch(`ApproveApp/` +id,{
            method:"PUT",
            headers:{
                Authorization: `Bearer ${user.access_token}`,
                "content-type":"application/json"}
        }).then((res)=>{
            return res.json();
        }).then((resp)=>{
            appdatachange(resp);
            console.log(resp);
            if (resp.status === 201) {
                localStorage.setItem("id", JSON.stringify(resp.data));

                return resp;
              }
        }).catch((err)=>{
            console.log(err.message);
        });


setShowDefault(true);
    };
    var id;
    function handleSubmit(e){
        e.preventDefault();
        id = appdata.id;
        console.log(id);
        fetch(`FeedApp/` +id,{
            method:"POST",
            headers:{
                Authorization: `Bearer ${user.access_token}`,
                "content-type":"application/json"},
                body:JSON.stringify(data)

        })
        .then(res=>{
           alert('application updated');

           history.push('/industry/evaluator1');

        }).catch((err) =>{
            console.log(err.message)
        })
    }
    useEffect(()=>{
      fetchData();
    },[]);



  return (
    <Card border="light" className="shadow-sm">
      <Card.Header>
        <Row className="align-items-center">
          <Col>
            <h5>Applications</h5>
          </Col>
          <Col className="text-end">
            <Button variant="secondary" size="sm">See all</Button>
          </Col>
        </Row>
      </Card.Header>
      <Table responsive className="align-items-center table-flush">
        <thead className="thead-light">
          <tr>
          <th scope="col">#</th>
            <th scope="col">Brand Name</th>
            <th scope="col">Product Form</th>
            <th scope="col">Intended Use</th>
            <th scope="col">Target User</th>
            <th scope="col">Shelf Life</th>
            <th scope="col">Storage Condition</th>
            <th scope="col">Food Additives</th>
            <th scope="col">Food Ingredient </th>
            <th scope="col">Status </th>
            <th scope="col">Stage </th>
            <th scope="col">Nutritional Information</th>
            <th scope="col">Action</th>

          </tr>
        </thead>
        <tbody>
        {userdata.map((item)=>(
                  <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.Brand_name}</td>
                  <td>{item.product_form}</td>
                  <td>{item.intended_use}</td>
                  <td>{item.target_user}</td>
                  <td>{item.shelf_life}</td>
                  <td>{item.storage_condition}</td>
                  <td>{item.food_additives}</td>
                  <td>{item.food_ingredient}</td>
                  <td className="text-danger" >{item.status}</td>
                  <td className="text-success" >{item.stage}</td>
                  <td>{item.nutritional_info}</td>
                  <td>
                      <Button  onClick={()=>{loadEdit(item.id)}} size="sm" variant ="success" className="m-1">Evaluate</Button>

                  </td>
                  </tr>
              ))}
        </tbody>
      </Table>
      <Modal as={Modal.Dialog} centered show={showDefault} onHide={handleClose}>
       <Modal.Header>
      <Modal.Title className="h6">Evaluation Form</Modal.Title>
      <Button variant="close" aria-label="Close" onClick={handleClose} />
    </Modal.Header>
    <Modal.Body>
    <Form className="mt-4" onSubmit={handleSubmit}>
                 <Form.Group className="mb-4" id="product_form" name="standards">
                <Form.Label>standards</Form.Label>
               <Form.Select  value={standards} onChange={e=>standchange(e.target.value)}>
              <option>High Risk Food</option>
              <option>Low Risk Food</option>

           </Form.Select>
          </Form.Group>
                  <Form.Group id="target_user" name="recommendation" className="mb-4" >
                    <Form.Label>recommendation</Form.Label>
                    <InputGroup>
                      <InputGroup.Text >
                        <FontAwesomeIcon icon={faEnvelope} />
                      </InputGroup.Text>
                      <Form.Control autoFocus required type="text" placeholder="" value={recomm} onChange={e=>recommchange(e.target.value)} />

                    </InputGroup>
                  </Form.Group>


                  <Form.Group id="storage_condition"  name="laboratory_analysis"  className="mb-4" >
                    <Form.Label>Laboratory Analysis</Form.Label>
                    <InputGroup>
                      <InputGroup.Text  >
                        <FontAwesomeIcon icon={faEnvelope} />
                      </InputGroup.Text>
                      <Form.Control autoFocus required type="text" placeholder="" value={lab} onChange={e=>labchange(e.target.value)}  />

                    </InputGroup>
                  </Form.Group>
                  <Form.Group id="food_ingredient" name="query"  className="mb-4" >
                    <Form.Label>Query</Form.Label>
                    <InputGroup>
                      <InputGroup.Text >
                        <FontAwesomeIcon icon={faUserCircle} />
                      </InputGroup.Text>
                      <Form.Control required type="text" placeholder="" value={query} onChange={e=>querychange(e.target.value)}  />

                    </InputGroup>
                  </Form.Group>

                  <Button variant="primary" type="submit" className="w-100">
                    Approve
                  </Button>
                </Form>


    </Modal.Body>
    <Modal.Footer>
      <Button variant="tertiary" onClick={handleClose}>
        I Got It
    </Button>
      <Button variant="link" className="text-gray ms-auto" onClick={handleClose}>
        Close
    </Button>
    </Modal.Footer>
  </Modal>
    </Card>
  );
};
